package com.example.duoihinhbatchu.model;

import com.example.duoihinhbatchu.PlayActivity;
import com.example.duoihinhbatchu.object.CauDo;
import com.example.duoihinhbatchu.object.NguoiDung;

import java.util.ArrayList;

public class ChoiGameModels {
    PlayActivity c;
    ArrayList<CauDo> arr;
    int cauSo = -1;
    public NguoiDung nguoiDung;

    public ChoiGameModels(PlayActivity c) {
        this.c = c;
        nguoiDung = new NguoiDung();
        taoData();
    }

    private void taoData() {
        arr = new ArrayList<>();
        arr.add(new CauDo("Man 1", "baola", "https://4.bp.blogspot.com/-Trb-aq5diQA/U5E_MYEFXsI/AAAAAAAASyY/dYF-ljVBv7A/s1600/10431540_671362626263774_1717932326842688214_n.jpg"));
        arr.add(new CauDo("Man 2", "namdinh", "data:image/jpeg;base64,"));
        arr.add(new CauDo("Man 3", "tienganh", "data:image/png;base64,"));

    }

    public CauDo layCauDo() {
        cauSo++;
        if (cauSo >= arr.size()) {
            cauSo = arr.size() - 1;
        }
        return arr.get(cauSo);
    }

    public void layTT() {
        nguoiDung.getTT(c);
    }

    public void luuTT() {
        nguoiDung.saveTT(c);
    }

}
